import React, { useState } from 'react';
import ReactMarkdown from 'react-markdown';
const GeminiChatBox = () => {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);

  const GEMINI_API_KEY = 'AIzaSyD0ZQiR56gas1Kxnq_fEwv3Ku9M_KH4Zo8'; // Replace this
  const GEMINI_ENDPOINT = 'https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-pro-latest:generateContent';



  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { role: 'user', content: input };
    const newMessages = [...messages, userMessage];
    setMessages(newMessages);
    setInput('');
    setLoading(true);

    try {
      const response = await fetch(`${GEMINI_ENDPOINT}?key=${GEMINI_API_KEY}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          contents: [{ role: 'user', parts: [{ text: input }] }]
        })
      });

      const data = await response.json();
      //console.log('Gemini response:', data);
      const reply = data?.candidates?.[0]?.content?.parts?.[0]?.text || 'No response from Gemini.';

      setMessages([...newMessages, { role: 'assistant', content: reply }]);
    } catch (error) {
      setMessages([...newMessages, { role: 'assistant', content: 'Error: ' + error.message }]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-xl mx-auto">
      <div className="border rounded p-4 h-96 overflow-y-scroll mb-4 bg-white shadow">
        {messages.map((msg, index) => (
          <div key={index} className={`mb-2 ${msg.role === 'user' ? 'text-right' : 'text-left'}`}>
  <span className={`inline-block px-3 py-2 rounded ${msg.role === 'user' ? 'bg-blue-200' : 'bg-gray-200'}`}>
    <ReactMarkdown>{msg.content}</ReactMarkdown>
  </span>
</div>
        ))}
        {loading && <p className="text-gray-500">Gemini is thinking...</p>}
      </div>
      <div className="flex gap-2">
        <input
          className="flex-1 border rounded p-2"
          type="text"
          placeholder="Type your message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
        />
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          onClick={sendMessage}
        >
          Send
        </button>
      </div>
      <span
  className={`inline-block px-3 py-2 rounded whitespace-pre-wrap ${
    msg.role === 'user' ? 'bg-blue-200' : 'bg-gray-200'
  }`}
>
  <ReactMarkdown>{msg.content}</ReactMarkdown>
</span>
    </div>
  );
};

export default GeminiChatBox;
